// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [stats, setStats] = useState({});
  const [weeklyRevenue, setWeeklyRevenue] = useState([]);
  const [serviceRevenue, setServiceRevenue] = useState([]);
  const [searchPhone, setSearchPhone] = useState("");
  const [visitorDetails, setVisitorDetails] = useState(null);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem("token");
      const headers = { headers: { Authorization: `Bearer ${token}` } };

      const res = await axios.get("http://localhost:8080/api/dashboard/stats", headers);
      setStats(res.data);

      const serviceRes = await axios.get("http://localhost:8080/api/dashboard/service-revenue", headers);
      setServiceRevenue(serviceRes.data);

      const weekRes = await axios.get("http://localhost:8080/api/dashboard/weekly-revenue", headers);
      setWeeklyRevenue(weekRes.data);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
    }
  };

  const handleSearchPhone = async () => {
    try {
      const token = localStorage.getItem("token");
      const headers = { headers: { Authorization: `Bearer ${token}` } };

      const res = await axios.get(`http://localhost:8080/api/dashboard/details/${searchPhone}`, headers);
      setVisitorDetails(res.data);
    } catch (error) {
      console.error("Error fetching visitor details:", error);
      setVisitorDetails(null);
    }
  };

  const weeklyBarData = {
    labels: weeklyRevenue.map(day => day.date),
    datasets: [
      {
        label: 'Revenue',
        data: weeklyRevenue.map(day => day.amount),
        backgroundColor: '#4e73df',
        barThickness: 30
      }
    ]
  };

  const servicePieData = {
    labels: serviceRevenue.map(s => s.serviceType),
    datasets: [
      {
        data: serviceRevenue.map(s => s.total),
        backgroundColor: ['#36b9cc', '#f6c23e', '#e74a3b', '#1cc88a']
      }
    ]
  };

  return (
    <div className="container-fluid p-4">
      <h2 className="mb-4">Dashboard</h2>

      <div className="row mb-4">
        <div className="col-md-3">
          <div className="bg-light p-3 rounded">
            <h6>Today</h6>
            <p>${stats.todayRevenue}</p>
          </div>
        </div>
        <div className="col-md-3">
          <div className="bg-light p-3 rounded">
            <h6>This Week</h6>
            <p>${stats.weekRevenue}</p>
          </div>
        </div>
        <div className="col-md-3">
          <div className="bg-light p-3 rounded">
            <h6>This Month</h6>
            <p>${stats.monthRevenue}</p>
          </div>
        </div>
      </div>

      <h5>Weekly Revenue</h5>
      <div className="mb-4" style={{ width: '800px', height: '500px', margin: '0 auto' }}>
        <Bar data={weeklyBarData} options={{ indexAxis: 'x' }} />
      </div>

      <h5>Revenue by Service</h5>
      <div style={{ width: '800px', height: '500px', margin: '0 auto' }}>
        <Pie data={servicePieData} />
      </div>

      <h5>Search by Phone Number</h5>
      <div className="d-flex gap-2 mb-3">
        <input className="form-control" value={searchPhone} onChange={(e) => setSearchPhone(e.target.value)} placeholder="Phone Number" />
        <button className="btn btn-primary" onClick={handleSearchPhone}>Search</button>
      </div>

      {visitorDetails && (
        <div>
          <h6>Visitor Info</h6>
          <p><strong>Name:</strong> {visitorDetails.name}</p>
          <p><strong>Phone:</strong> {visitorDetails.phoneNumber}</p>
          <p><strong>Address:</strong> {visitorDetails.address}</p>

          <h6>Visits</h6>
          <ul>
            {visitorDetails.visits.map((visit, idx) => (
              <li key={idx}>Check-in: {visit.inTime} | Check-out: {visit.outTime || 'Ongoing'}</li>
            ))}
          </ul>

          <h6>Payments</h6>
          <ul>
            {visitorDetails.payments.map((pay, idx) => (
              <li key={idx}>{pay.category} - {pay.serviceType} - ${pay.amount}</li>
            ))}
          </ul>

          <h6>Services</h6>
          <ul>
            {visitorDetails.services.map((srv, idx) => (
              <li key={idx}>{srv.serviceType} on {srv.timestamp}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
