// Modified Dashboard.jsx with drill-down behavior for Today/Week/Month card clicks
import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Bar, Pie } from 'react-chartjs-2';
import { getAuthConfig } from "../utils/Auth";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [stats, setStats] = useState({});
  const [weeklyRevenue, setWeeklyRevenue] = useState([]);
  const [serviceRevenue, setServiceRevenue] = useState([]);
  const [searchPhone, setSearchPhone] = useState("");
  const [visitorDetails, setVisitorDetails] = useState(null);
  const [lookupError, setLookupError] = useState("");

  const [registerUsername, setRegisterUsername] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [registerRole, setRegisterRole] = useState("RECEPTIONIST");
  const [registerMsg, setRegisterMsg] = useState("");

  const [transactionFilter, setTransactionFilter] = useState(null); // 'today' | 'week' | 'month'
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  useEffect(() => {
    if (transactionFilter) {
      fetchTransactions(transactionFilter);
    }
  }, [transactionFilter]);

  const fetchDashboardStats = async () => {
    try {
      const config = getAuthConfig();
      const statsRes = await axios.get("http://localhost:8080/api/dashboard/stats", config);
      const serviceRes = await axios.get("http://localhost:8080/api/dashboard/service-revenue", config);
      const weekRes = await axios.get("http://localhost:8080/api/dashboard/weekly-revenue", config);
      setStats(statsRes.data);
      setServiceRevenue(serviceRes.data);
      setWeeklyRevenue(weekRes.data);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
    }
  };

  const fetchTransactions = async (filter) => {
    try {
      const config = getAuthConfig();
      const res = await axios.get(`http://localhost:8080/api/dashboard/transactions?period=${filter}`, config);
      setTransactions(res.data);
    } catch (error) {
      console.error("Error fetching transactions:", error);
    }
  };

  const handleCardClick = (filter) => {
    setTransactionFilter(filter);
  };

  const renderTransactionTable = () => (
    <div>
      <h5 className="mt-4">Transactions - {transactionFilter.toUpperCase()}</h5>
      {transactions.length === 0 ? (
        <p>No transactions found.</p>
      ) : (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Visitor</th>
              <th>Service</th>
              <th>Amount</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((tx, idx) => (
              <tr key={idx}>
                <td>{tx.visitorName}</td>
                <td>{tx.serviceType}</td>
                <td>${tx.amount}</td>
                <td>{tx.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );

  return (
    <div className="d-flex">
      <div className="bg-dark text-white p-3" style={{ width: '220px', minHeight: '100vh' }}>
        <ul className="nav flex-column">
          <li className="nav-item mb-2">
            <button className={`btn btn-link nav-link text-white ${activeTab === 'dashboard' ? 'fw-bold' : ''}`} onClick={() => setActiveTab('dashboard')}>Dashboard</button>
          </li>
          <li className="nav-item mb-2">
            <button className={`btn btn-link nav-link text-white ${activeTab === 'visitors' ? 'fw-bold' : ''}`} onClick={() => setActiveTab('visitors')}>Visitors</button>
          </li>
          <li className="nav-item mb-2">
            <button className={`btn btn-link nav-link text-white ${activeTab === 'individual' ? 'fw-bold' : ''}`} onClick={() => setActiveTab('individual')}>Lookup</button>
          </li>
          <li className="nav-item mb-2">
            <button className={`btn btn-link nav-link text-white ${activeTab === 'register' ? 'fw-bold' : ''}`} onClick={() => setActiveTab('register')}>Register User</button>
          </li>
        </ul>
      </div>

      <div className="container-fluid p-4">
        {activeTab === "dashboard" && (
          <div>
            <h2 className="mb-4">Dashboard</h2>
            <div className="row mb-4">
              <div className="col-md-3">
                <div className="bg-light p-3 rounded cursor-pointer" onClick={() => handleCardClick("today")}> <h6>Today</h6> <p>${stats.todayRevenue}</p> </div>
              </div>
              <div className="col-md-3">
                <div className="bg-light p-3 rounded cursor-pointer" onClick={() => handleCardClick("week")}> <h6>This Week</h6> <p>${stats.weekRevenue}</p> </div>
              </div>
              <div className="col-md-3">
                <div className="bg-light p-3 rounded cursor-pointer" onClick={() => handleCardClick("month")}> <h6>This Month</h6> <p>${stats.monthRevenue}</p> </div>
              </div>
            </div>

            <h5>Weekly Revenue</h5>
            <div className="mb-4" style={{ width: '100%', maxWidth: '800px', margin: '0 auto' }}>
              <Bar data={{
                labels: weeklyRevenue.map(d => d.date),
                datasets: [{ label: 'Revenue', data: weeklyRevenue.map(d => d.amount), backgroundColor: '#4e73df' }]
              }} />
            </div>

            <h5>Revenue by Service</h5>
            <div style={{ width: '100%', maxWidth: '600px', margin: '0 auto' }}>
              <Pie data={{
                labels: serviceRevenue.map(s => s.serviceType),
                datasets: [{
                  data: serviceRevenue.map(s => s.total),
                  backgroundColor: ['#36b9cc', '#f6c23e', '#e74a3b', '#1cc88a']
                }]
              }} />
            </div>

            {transactionFilter && renderTransactionTable()}
          </div>
        )}
      </div>
    </div>
  );
}
