import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

// Add interceptor to include JWT in all requests
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  error => Promise.reject(error)
);

export default function ReceptionistPanel() {
  const [visitors, setVisitors] = useState([]);
  const [newVisitorName, setNewVisitorName] = useState("");
  const [newVisitorPhone, setNewVisitorPhone] = useState("");
  const [newVisitorRole, setNewVisitorRole] = useState("Customer");
  const [newVisitorAddress, setNewVisitorAddress] = useState("");
  const [checkInPhone, setCheckInPhone] = useState("");
  const [visitId, setVisitId] = useState(null);
  const [serviceType, setServiceType] = useState("");
  const [serviceName, setServiceName] = useState("");
  const [servicePhone, setServicePhone] = useState("");
  const [paymentName, setPaymentName] = useState("");
  const [paymentPhone, setPaymentPhone] = useState("");
  const [paymentAmount, setPaymentAmount] = useState("");
  const [paymentCategory, setPaymentCategory] = useState("");
  const [paymentServiceType, setPaymentServiceType] = useState("");
  const [activeSection, setActiveSection] = useState("register");

  useEffect(() => {
    fetchVisitors();
  }, []);

  const fetchVisitors = async () => {
    const response = await axios.get("http://localhost:8080/api/visitors");
    setVisitors(response.data);
  };

  const handleRegisterVisitor = async () => {
    await axios.post("http://localhost:8080/api/visitors/register", {
      name: newVisitorName,
      phoneNumber: newVisitorPhone,
      address: newVisitorAddress,
      role: newVisitorRole
    });
    alert("Visitor registered");
    setNewVisitorName("");
    setNewVisitorPhone("");
    setNewVisitorAddress("");
    setNewVisitorRole("Customer");
    fetchVisitors();
  };

  const handleCheckIn = async () => {
    const response = await axios.post("http://localhost:8080/api/visits/checkin", { phoneNumber: checkInPhone });
    alert("Checked in");
    setVisitId(response.data.id);
  };

  const handleCheckOut = async () => {
    await axios.post(`http://localhost:8080/api/visits/checkout/${checkInPhone}`);
    alert("Checked out");
  };

  const handleAddService = async () => {
    await axios.post("http://localhost:8080/api/services", {
      phoneNumber: servicePhone,
      name: serviceName,
      serviceType
    });
    alert("Service added");
  };

  const handleAddPayment = async () => {
    await axios.post("http://localhost:8080/api/payments", {
      phoneNumber: paymentPhone,
      name: paymentName,
      category: paymentCategory,
      serviceType: paymentServiceType,
      amount: parseFloat(paymentAmount)
    });
    alert("Payment recorded");
  };

  return (
    <div className="container py-4" style={{ backgroundImage: 'url("https://www.transparenttextures.com/patterns/white-wall-3.png")', backgroundColor: '#fff' }}>
      <h1 className="mb-4 text-center">Visitor Ingestion</h1>

      <ul className="nav nav-tabs mb-3">
        <li className="nav-item">
          <button className={`nav-link ${activeSection === 'register' ? 'active' : ''}`} onClick={() => setActiveSection('register')}>Register</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeSection === 'checkin' ? 'active' : ''}`} onClick={() => setActiveSection('checkin')}>Check-In/Out</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeSection === 'service' ? 'active' : ''}`} onClick={() => setActiveSection('service')}>Service</button>
        </li>
        <li className="nav-item">
          <button className={`nav-link ${activeSection === 'payment' ? 'active' : ''}`} onClick={() => setActiveSection('payment')}>Payment</button>
        </li>
      </ul>

      {activeSection === 'register' && (
        <div className="card mb-3">
          <div className="card-header">Register Visitor</div>
          <div className="card-body">
            <div className="mb-2"><input className="form-control" placeholder="Visitor Name" value={newVisitorName} onChange={(e) => setNewVisitorName(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Phone Number" value={newVisitorPhone} onChange={(e) => setNewVisitorPhone(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Address" value={newVisitorAddress} onChange={(e) => setNewVisitorAddress(e.target.value)} /></div>
            <div className="text-center">
              <button className="btn btn-dark" onClick={handleRegisterVisitor}>Register</button>
            </div>
          </div>
        </div>
      )}

      {activeSection === 'checkin' && (
        <div className="card mb-3">
          <div className="card-header">Check-In / Check-Out</div>
          <div className="card-body d-flex gap-3 flex-wrap">
            <input className="form-control w-auto" placeholder="Phone Number" value={checkInPhone} onChange={(e) => setCheckInPhone(e.target.value)} />
            <button className="btn btn-success" onClick={handleCheckIn}>Check In</button>
            <button className="btn btn-danger" onClick={handleCheckOut}>Check Out</button>
          </div>
        </div>
      )}

      {activeSection === 'service' && (
        <div className="card mb-3">
          <div className="card-header">Add Service</div>
          <div className="card-body">
            <div className="mb-2"><input className="form-control" placeholder="Phone Number" value={servicePhone} onChange={(e) => setServicePhone(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Name" value={serviceName} onChange={(e) => setServiceName(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Service Type" value={serviceType} onChange={(e) => setServiceType(e.target.value)} /></div>
            <div className="text-center">
              <button className="btn btn-primary" onClick={handleAddService}>Add Service</button>
            </div>
          </div>
        </div>
      )}

      {activeSection === 'payment' && (
        <div className="card mb-3">
          <div className="card-header">Add Payment</div>
          <div className="card-body">
            <div className="mb-2"><input className="form-control" placeholder="Name" value={paymentName} onChange={(e) => setPaymentName(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Phone Number" value={paymentPhone} onChange={(e) => setPaymentPhone(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Category" value={paymentCategory} onChange={(e) => setPaymentCategory(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Service Type" value={paymentServiceType} onChange={(e) => setPaymentServiceType(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" type="number" placeholder="Amount" value={paymentAmount} onChange={(e) => setPaymentAmount(e.target.value)} /></div>
            <div className="text-center">
              <button className="btn btn-warning" onClick={handleAddPayment}>Add</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
